import { Link } from 'react-router-dom';
import React,{Component} from 'react'; 
import './uploadImages.css'
var listOfImages =[];


class UploadImages extends Component { 

    
    importAll(r) {
        return r.keys().map(r);
    }
     componentDidMount()
     {
         document.body.classList.add('bg-account-pages');
         document.body.classList.add('py-4');
         document.body.classList.add('py-sm-0');
     }
    componentWillMount() {
        listOfImages = this.importAll(require.context('./images/', false, /\.(png|jpe?g|svg)$/));
    }
    
        constructor(props) {
          super(props);
          this.state = {
            file: '',
            imagePreviewUrl: ''
          };
          this._handleImageChange = this._handleImageChange.bind(this);
          this._handleSubmit = this._handleSubmit.bind(this);
        }
      
        _handleSubmit(e) {
          e.preventDefault();
          // TODO: do something with -> this.state.file
          console.log(this.state.file)
          
          
          
        }
      
        _handleImageChange(e) {
          e.preventDefault();
      
          let reader = new FileReader();
          let file = e.target.files[0];
      
          reader.onloadend = () => {
            this.setState({
              file: file,
              imagePreviewUrl: reader.result
            });
          }
      
          reader.readAsDataURL(file)
        }
      
        render() {
          let {imagePreviewUrl} = this.state;
          let $imagePreview = null;
          if (imagePreviewUrl) {
            $imagePreview = (<img className="pre" src={imagePreviewUrl} alt="info"/>);
          }
      
          return (
            <div>
                <div className="account-home-btn d-none d-sm-block">
                    <Link to="/" className="text-white"><i className="mdi mdi-home h1"></i></Link>
                </div>

                <div className='onleft'>
                    <form onSubmit={this._handleSubmit}>
                        <input type="file" onChange={this._handleImageChange} />
                        <button type="submit" onClick={this._handleSubmit}>Upload Image</button>
                    </form>
                        {$imagePreview} 
                </div>

                <div className="ex1">
              {
                    listOfImages.map(
                      (image, index) =>    <img className="responsive"   key={index} src={image} alt="infoo"></img>
                    )
              }
          </div>             
            </div>
            
          )
        }
      
      }
  
  export default UploadImages;
