import React from 'react'
import './forms/form.css'
const axios = require("axios");

class uploadImages2 extends React.Component {

    constructor(props) {
        super(props);
        this.state ={
            file: null
        };
        this.onFormSubmit = this.onFormSubmit.bind(this);
        this.onChange = this.onChange.bind(this);
    }
    onFormSubmit(e){
        e.preventDefault();
          var tok =localStorage.getItem('Token');
          console.log(localStorage.getItem('Username'));
  var bea="bearer "
  var result = bea+tok;
  console.log(result);
        const formData = new FormData();
        formData.append('imageFile',this.state.file);
        const config = {
            headers: {
                'content-type': 'multipart/form-data',
                "Authorization":result
            }
        };
        axios.post("http://localhost:9000/mycontent",formData,config)
            .then((res) => {
                console.log(res.data);
            }).catch((error) => { console.log(error)
        });
    }
    onChange(e) {
        this.setState({file:e.target.files[0]});
    }

    render() {
        return (
            <div >
            <div class="uframe">
             <div class="ucenter">
              <div class="utitle">
			    <h1 class="uh1">Choose file to upload</h1>
		      </div>
              
            <form onSubmit={this.onFormSubmit}>
               
                <input type="file" name="myImage" onChange= {this.onChange} />
                <button type="submit" class="ubtn">Upload</button>
            </form>
           
             </div>
            </div>
            </div>
        )
    }
}
  
  export default uploadImages2;
  
